const aiOutput = $json.output.Content || '';
const maxLength = 4000; // Leave some buffer for Telegram's 4096 limit

let finalMessage = aiOutput;

// If message is too long, intelligently truncate
if (finalMessage.length > maxLength) {
  // Try to truncate at a sentence boundary
  const truncated = finalMessage.substring(0, maxLength);
  const lastSentence = truncated.lastIndexOf('.');
  const lastNewline = truncated.lastIndexOf('\n');

  // Find the best place to cut
  const cutPoint = Math.max(lastSentence, lastNewline);

  if (cutPoint > maxLength * 0.8) {
    // Good cut point found
    finalMessage = finalMessage.substring(0, cutPoint + 1) + "\n\n📝 [Full post truncated for Telegram]";
  } else {
    // No good cut point, just truncate with ellipsis
    finalMessage = finalMessage.substring(0, maxLength - 50) + "...\n\n📝 [Message truncated due to length]";
  }
}

return { 
  formatted_message: finalMessage,
  original_length: aiOutput.length,
  was_truncated: aiOutput.length > maxLength
};

